mapper.insert:
update student
set violation_count = violation_count + 1,
violation_score = violation_score + #{score}
where id = #{id}

mapper.select:
select student.*, clazz.name clazzName
from student left join clazz on student.clazz_id = clazz.id


        and student.name like concat('%',#{name},'%')


        and student.degree = #{degree}


        and student.clazz_id = #{clazzId}


order by student.update_time desc

select student.*, clazz.name clazzName
from student left join clazz on student.clazz_id = clazz.id
where student.id = #{id}
